.attbtn {
    border:1px solid #AAAAAA;
    margin-left: 2px;
    margin-right: 2px;
    padding: 5px;
    border-radius: 5px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    -opera-border-radius: 5px;
    -khtml-border-radius: 5px;
}

.attcurbtn {
    margin-left: 2px;
    margin-right: 2px;
    padding: 5px;
}

.attfiltercontrols {
    margin-bottom: 10px;
    margin-right:auto;
    margin-left:auto;
}

.attwidth {
    width:90%;
}

body#mod-attforblock-attendances table.controls {
    width: 100%;
}

body#mod-attforblock-attendances table.controls {
    width: 100%;
}

body#mod-attforblock-attendances table.controls tr {
    vertical-align: top;
}

body#mod-attforblock-attendances table.controls td.right, body#mod-attforblock-attendances table.controls td.left {
    padding-bottom: 4px;
    padding-left: 4px;
    padding-right: 4px;
    padding-top: 4px;
}

body#mod-attforblock-attendances table.controls .right {
    text-align: right;
}
/* for IE7*/
.attfiltercontrols td {
    padding:6px;
}

